        logRequestDetails(request)
        next()
      },
      (request, response, next) => {
        response.send('POST request successful!')
      }
    )

  app
    .route('/contact/:contactId')
    .put(
      (request, response, next) => {
        //middleware
        logRequestDetails(request)
        next()
      },
      (request, response, next) => {
        response.send('PUT request successful!')
      }
